  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'org-settings',
	title: 'Organization Settings',
	sidebar_label: 'Organization Settings'
};
export const metadata = {
  "unversionedId": "basics/acct-team-mgmt/org-settings",
  "id": "basics/acct-team-mgmt/org-settings",
  "isDocsHomePage": false,
  "title": "Organization Settings",
  "description": "General Settings",
  "source": "@site/docs/basics/acct-team-mgmt/org-settings.md",
  "slug": "/basics/acct-team-mgmt/org-settings",
  "permalink": "/basics/acct-team-mgmt/org-settings",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/basics/acct-team-mgmt/org-settings.md",
